import { useState } from 'react';
import { Leaf, Sparkles, BookOpen, Sun, Droplets, Clock, X } from 'lucide-react';

const plantsData = [
  {
    id: 1,
    name: "Tulsi",
    tips: "Tulsi thrives in warm climates with well-drained soil and sunlight.",
    history: "Used in Ayurvedic medicine for centuries and considered sacred in Indian households.",
    image: "https://images.unsplash.com/photo-1546617575-e8e25c498c3e?w=400",
    category: "Sacred Herbs"
  },
  {
    id: 2,
    name: "Neem",
    tips: "Needs full sunlight, tolerates dry and poor soil, and should be watered deeply once a week.",
    history: "Known for its antibacterial and medicinal properties. Widely used in Ayurvedic treatments and natural pest control.",
    image: "https://images.unsplash.com/photo-1584641385949-6ffeaa0c4ba6?w=400",
    category: "Medicinal Trees"
  },
  {
    id: 3,
    name: "Aloe Vera",
    tips: "Prefers dry conditions and indirect sunlight. Water sparingly.",
    history: "Aloe has been used since ancient Egypt for healing wounds and skin care.",
    image: "https://images.unsplash.com/photo-1509587584298-59b15867a245?w=400",
    category: "Healing Plants"
  },
  {
    id: 4,
    name: "Ashwagandha",
    tips: "Grows best in dry regions with well-drained soil and full sunlight.",
    history: "Used in Ayurveda as a rejuvenator and stress reliever for over 3,000 years.",
    image: "https://images.unsplash.com/photo-1571019613454-1cb2f99b2d8b?w=400",
    category: "Adaptogens"
  },
  {
    id: 5,
    name: "Moringa",
    tips: "Moringa prefers hot climates and grows well in sandy or loamy soil with good drainage. Water moderately.",
    history: "Revered as the 'Miracle Tree', Moringa has been used in Ayurvedic medicine for over 4,000 years for its nutritional and healing properties.",
    image: "https://images.unsplash.com/photo-1578662996442-48f60103fc96?w=400",
    category: "Superfoods"
  },
  {
    id: 6,
    name: "Mint",
    tips: "Grows rapidly in moist, partly shaded areas. Regular trimming keeps it bushy.",
    history: "Widely used in traditional medicine and cooking across cultures.",
    image: "https://images.unsplash.com/photo-1628556270448-4d4e4148e1b1?w=400",
    category: "Culinary Herbs"
  },
  {
    id: 7,
    name: "Lemongrass",
    tips: "Thrives in tropical climates with full sun and frequent watering.",
    history: "Used in herbal teas and medicine to relieve anxiety and digestive issues.",
    image: "https://images.unsplash.com/photo-1609501676725-7186f669ac28?w=400",
    category: "Aromatics"
  },
  {
    id: 8,
    name: "Hibiscus",
    tips: "Grows best in tropical conditions with full sunlight and moist, well-drained soil. Prune regularly to encourage flowering.",
    history: "Traditionally used in herbal teas and hair care in Indian and Egyptian cultures. Red hibiscus is associated with the goddess Kali in Hinduism.",
    image: "https://images.unsplash.com/photo-1615671524827-c1fe3973b648?w=400",
    category: "Flowering Plants"
  },
  {
    id: 9,
    name: "Fenugreek",
    tips: "Best grown in cool climates with well-drained soil. Requires regular watering.",
    history: "Has been cultivated since ancient times for both culinary and medicinal uses, especially for digestion.",
    image: "https://images.unsplash.com/photo-1581833971358-2c8b550f87b3?w=400",
    category: "Spice Herbs"
  },
  {
    id: 10,
    name: "Clove",
    tips: "Clove trees thrive in warm, humid climates with well-drained, rich loamy soil. They need partial shade and regular watering during dry periods.",
    history: "Cloves have been used in Ayurveda and Chinese medicine for centuries. They are known for antiseptic and digestive properties.",
    image: "https://images.unsplash.com/photo-1609735644742-c89f59fc4e6f?w=400",
    category: "Aromatic Spices"
  },
  {
    id: 11,
    name: "Coriander",
    tips: "Needs loose, well-drained soil and moderate sunlight.",
    history: "Used since ancient Egypt for culinary and medicinal purposes.",
    image: "https://images.unsplash.com/photo-1608897013039-887f21d8c804?w=400",
    category: "Culinary Herbs"
  },
  {
    id: 12,
    name: "Curry Leaves",
    tips: "Prefers a sunny location and well-drained soil. Water moderately and trim occasionally to encourage bushy growth.",
    history: "Deeply rooted in South Indian cuisine and Ayurveda, curry leaves are known for improving digestion and enhancing flavor.",
    image: "https://images.unsplash.com/photo-1596040033229-a9821ebd058d?w=400",
    category: "Culinary Essentials"
  }
];

export default function Cultivation() {
  const [selectedPlant, setSelectedPlant] = useState(null);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [filter, setFilter] = useState('All');

  const categories = ['All', ...new Set(plantsData.map(plant => plant.category))];
  
  const filteredPlants = filter === 'All' 
    ? plantsData 
    : plantsData.filter(plant => plant.category === filter);

  const openModal = (plant) => {
    setSelectedPlant(plant);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedPlant(null);
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-emerald-50 via-green-50 to-teal-50 relative overflow-hidden">
      {/* Animated Background Elements */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        <div className="absolute -top-40 -right-40 w-96 h-96 bg-gradient-to-r from-green-200 to-emerald-300 rounded-full mix-blend-multiply filter blur-3xl opacity-30 animate-pulse"></div>
        <div className="absolute -bottom-40 -left-40 w-96 h-96 bg-gradient-to-r from-teal-200 to-green-300 rounded-full mix-blend-multiply filter blur-3xl opacity-30 animate-pulse" style={{animationDelay: '2s'}}></div>
        <div className="absolute top-1/4 left-1/4 w-32 h-32 bg-emerald-200 rounded-full mix-blend-multiply filter blur-2xl opacity-25 animate-bounce" style={{animationDelay: '1s'}}></div>
        <div className="absolute bottom-1/4 right-1/4 w-24 h-24 bg-green-300 rounded-full mix-blend-multiply filter blur-2xl opacity-30 animate-pulse" style={{animationDelay: '3s'}}></div>
      </div>

      {/* Floating Particles */}
      <div className="absolute inset-0 overflow-hidden pointer-events-none">
        {[...Array(6)].map((_, i) => (
          <div
            key={i}
            className="absolute w-2 h-2 bg-green-400 rounded-full opacity-20 animate-float"
            style={{
              left: `${Math.random() * 100}%`,
              top: `${Math.random() * 100}%`,
              animationDelay: `${i * 0.5}s`,
              animationDuration: `${3 + Math.random() * 2}s`
            }}
          />
        ))}
      </div>

      <div className="relative z-10 px-4 py-8">
        {/* Header */}
        <div className="text-center mb-16">
          <div className="inline-flex items-center justify-center w-24 h-24 bg-gradient-to-r from-green-500 via-emerald-500 to-teal-500 rounded-full mb-8 shadow-2xl animate-pulse">
            <Leaf className="w-12 h-12 text-white animate-bounce" />
          </div>
          
          <h1 className="text-6xl md:text-8xl font-extrabold bg-gradient-to-r from-green-600 via-emerald-500 to-teal-600 bg-clip-text text-transparent mb-6 font-serif tracking-tight">
            Virtual Herbal Garden
          </h1>
          
          <p className="text-xl md:text-2xl text-gray-600 max-w-3xl mx-auto leading-relaxed mb-8">
            Discover the ancient wisdom of cultivation and the rich historical tapestry of medicinal herbs
          </p>
          
          <div className="flex items-center justify-center space-x-8 text-emerald-600">
            <div className="flex items-center">
              <Sparkles className="w-5 h-5 mr-2 animate-spin" />
              <span className="text-sm font-medium">Plant Cultivation</span>
            </div>
            <div className="flex items-center">
              <BookOpen className="w-5 h-5 mr-2" />
              <span className="text-sm font-medium">Historical Significance</span>
            </div>
          </div>
        </div>

        {/* Filter Tabs */}
        <div className="max-w-6xl mx-auto mb-12">
          <div className="flex flex-wrap justify-center gap-3">
            {categories.map((category) => (
              <button
                key={category}
                onClick={() => setFilter(category)}
                className={`px-6 py-3 rounded-full font-semibold text-sm transition-all duration-300 transform hover:scale-105 ${
                  filter === category
                    ? 'bg-gradient-to-r from-green-500 to-emerald-600 text-white shadow-lg'
                    : 'bg-white/70 backdrop-blur-sm text-gray-600 hover:bg-white/90 border border-white/20'
                }`}
              >
                {category}
              </button>
            ))}
          </div>
        </div>

        {/* Plants Grid */}
        <div className="max-w-7xl mx-auto">
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 xl:grid-cols-4 gap-8">
            {filteredPlants.map((plant, index) => (
              <div
                key={plant.id}
                className="group cursor-pointer transform transition-all duration-700 hover:scale-105 animate-fadeInUp"
                style={{ animationDelay: `${index * 0.1}s` }}
                onClick={() => openModal(plant)}
              >
                <div className="relative bg-white/80 backdrop-blur-lg rounded-3xl overflow-hidden shadow-xl border border-white/30 hover:shadow-2xl hover:bg-white/90 transition-all duration-500">
                  {/* Image Container */}
                  <div className="relative h-48 overflow-hidden">
                    <img
                      src={plant.image}
                      alt={plant.name}
                      className="w-full h-full object-cover transition-transform duration-700 group-hover:scale-110"
                    />
                    <div className="absolute inset-0 bg-gradient-to-t from-black/50 via-transparent to-transparent opacity-0 group-hover:opacity-100 transition-opacity duration-300"></div>
                    
                    {/* Category Badge */}
                    <div className="absolute top-3 left-3">
                      <span className="px-3 py-1 bg-emerald-500/90 backdrop-blur-sm text-white text-xs font-semibold rounded-full">
                        {plant.category}
                      </span>
                    </div>

                    {/* Floating Icons */}
                    <div className="absolute top-3 right-3 opacity-0 group-hover:opacity-100 transition-all duration-300 transform translate-y-2 group-hover:translate-y-0">
                      <div className="flex space-x-2">
                        <div className="w-8 h-8 bg-white/90 backdrop-blur-sm rounded-full flex items-center justify-center">
                          <Sun className="w-4 h-4 text-orange-500" />
                        </div>
                        <div className="w-8 h-8 bg-white/90 backdrop-blur-sm rounded-full flex items-center justify-center">
                          <Droplets className="w-4 h-4 text-blue-500" />
                        </div>
                      </div>
                    </div>
                  </div>

                  {/* Content */}
                  <div className="p-6">
                    <h3 className="text-2xl font-bold text-gray-800 mb-3 group-hover:text-emerald-600 transition-colors duration-300">
                      {plant.name}
                    </h3>
                    
                    {/* Tips Preview */}
                    <div className="flex items-start mb-3">
                      <Sun className="w-4 h-4 text-orange-500 mr-2 mt-1 flex-shrink-0" />
                      <p className="text-gray-600 text-sm line-clamp-2">
                        {plant.tips}
                      </p>
                    </div>

                    {/* History Preview */}
                    <div className="flex items-start mb-4">
                      <Clock className="w-4 h-4 text-emerald-500 mr-2 mt-1 flex-shrink-0" />
                      <p className="text-gray-600 text-sm line-clamp-2">
                        {plant.history}
                      </p>
                    </div>

                    {/* Learn More Button */}
                    <div className="flex items-center justify-between">
                      <span className="text-emerald-600 font-semibold text-sm group-hover:text-emerald-700 transition-colors duration-300">
                        Learn More
                      </span>
                      <div className="w-8 h-8 bg-gradient-to-r from-green-400 to-emerald-500 rounded-full flex items-center justify-center transform group-hover:scale-110 transition-transform duration-300">
                        <BookOpen className="w-4 h-4 text-white" />
                      </div>
                    </div>
                  </div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* Modal */}
      {isModalOpen && selectedPlant && (
        <div className="fixed inset-0 bg-black/70 backdrop-blur-sm flex items-center justify-center p-4 z-50">
          <div className="bg-white/95 backdrop-blur-lg rounded-3xl max-w-4xl w-full max-h-[90vh] overflow-y-auto border border-white/20 shadow-2xl animate-slideUp">
            <div className="relative">
              {/* Close Button */}
              <button
                onClick={closeModal}
                className="absolute top-6 right-6 z-10 bg-white/80 backdrop-blur-sm rounded-full p-3 hover:bg-white transition-all duration-300 shadow-lg"
              >
                <X className="w-6 h-6 text-gray-600" />
              </button>

              {/* Modal Content */}
              <div className="grid md:grid-cols-2 gap-8 p-8">
                {/* Image Section */}
                <div className="relative">
                  <div className="h-80 md:h-96 rounded-2xl overflow-hidden shadow-xl">
                    <img
                      src={selectedPlant.image}
                      alt={selectedPlant.name}
                      className="w-full h-full object-cover"
                    />
                    <div className="absolute inset-0 bg-gradient-to-t from-black/30 to-transparent"></div>
                  </div>
                  
                  {/* Category Badge */}
                  <div className="absolute top-4 left-4">
                    <span className="px-4 py-2 bg-emerald-500/90 backdrop-blur-sm text-white font-semibold rounded-full">
                      {selectedPlant.category}
                    </span>
                  </div>
                </div>

                {/* Content Section */}
                <div className="space-y-6">
                  <div>
                    <h2 className="text-4xl font-bold text-gray-800 mb-2">
                      {selectedPlant.name}
                    </h2>
                    <p className="text-emerald-600 font-semibold text-lg">
                      {selectedPlant.category}
                    </p>
                  </div>

                  {/* Cultivation Tips */}
                  <div className="bg-gradient-to-r from-orange-50 to-yellow-50 p-6 rounded-2xl border border-orange-100">
                    <div className="flex items-center mb-3">
                      <Sun className="w-6 h-6 text-orange-500 mr-3" />
                      <h3 className="text-xl font-bold text-gray-800">Cultivation Tips</h3>
                    </div>
                    <p className="text-gray-700 leading-relaxed">
                      {selectedPlant.tips}
                    </p>
                  </div>

                  {/* Historical Significance */}
                  <div className="bg-gradient-to-r from-emerald-50 to-teal-50 p-6 rounded-2xl border border-emerald-100">
                    <div className="flex items-center mb-3">
                      <Clock className="w-6 h-6 text-emerald-500 mr-3" />
                      <h3 className="text-xl font-bold text-gray-800">Historical Significance</h3>
                    </div>
                    <p className="text-gray-700 leading-relaxed">
                      {selectedPlant.history}
                    </p>
                  </div>

                  {/* Action Button */}
                  <button
                    onClick={closeModal}
                    className="w-full bg-gradient-to-r from-green-500 to-emerald-600 text-white py-4 px-6 rounded-2xl hover:from-green-600 hover:to-emerald-700 transition-all duration-300 font-bold text-lg shadow-lg hover:shadow-xl transform hover:scale-105"
                  >
                    Continue Exploring
                  </button>
                </div>
              </div>
            </div>
          </div>
        </div>
      )}

      <style jsx>{`
        @keyframes fadeInUp {
          0% {
            opacity: 0;
            transform: translateY(30px);
          }
          100% {
            opacity: 1;
            transform: translateY(0);
          }
        }

        @keyframes slideUp {
          0% {
            opacity: 0;
            transform: translateY(50px) scale(0.95);
          }
          100% {
            opacity: 1;
            transform: translateY(0) scale(1);
          }
        }

        @keyframes float {
          0%, 100% {
            transform: translateY(0px);
          }
          50% {
            transform: translateY(-20px);
          }
        }

        .animate-fadeInUp {
          animation: fadeInUp 0.6s ease-out forwards;
        }

        .animate-slideUp {
          animation: slideUp 0.4s ease-out forwards;
        }

        .animate-float {
          animation: float 3s ease-in-out infinite;
        }

        .line-clamp-2 {
          display: -webkit-box;
          -webkit-line-clamp: 2;
          -webkit-box-orient: vertical;
          overflow: hidden;
        }
      `}</style>
    </div>
  );
}
